import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [
        {
          name: 'Frankenstein',
          id: 0
        },
        {
          name: 'Dracula',
          id: 1
        },
        {
          name: 'Zombie',
          id: 2
        }
      ]
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(data=>data.json())
    .then(bsr=>console.log(bsr))
  }

  render() {
    return (
      <div className="App">
        {
          this.state.monsters.map(monster => <h1 key={monster.id}> { monster.name } </h1>)
        }
      </div>
    );
  }
}

export default App;
